mapper.select:
SELECT COUNT(ID)
  FROM MEMBER
 WHERE ID = #{id}

SELECT NO, ID, PW, NAME, EMAIL, REGDATE 
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}

mapper.update:
UPDATE MEMBER
   SET NAME = #{name}, EMAIL = #{email}
 WHERE NO = #{no}

UPDATE MEMBER
   SET PW = #{pw}
 WHERE NO = #{no}

mapper.select:
SELECT ID, REGDATE
  FROM MEMBER
 WHERE EMAIL = #{email}
